-- Playlist Switcher by LUA
-- 23/01/2022 07:37:58 UTC+3 Europe/Istanbul: Turkey
-- @alfaiptvserver

obs = obslua
source_name = ""
luafileTitle = "Playlist Switcher"
fversion = "1.0.0.0"
luafile = "playlist_switcher.lua"
last_state = obs.OBS_MEDIA_STATE_NONE
icon =
    "data:image/png;base64,"
desc =
    [[
<center><h2>]] ..
    luafileTitle ..
        [[</h2>( Version: %s )</center>
<br><center><img width=50 height=50 src=']] ..
            icon ..
                [['/></center>
<br><center><a href="https://github.com/alfaiptvserver/">GitHub</a></center>
<br><p>Genel Ayarlar</p>
<p>Seçilen kaynaktaki akış kapatıldıktan sonra oynatma listesinizdeki bir sonraki içeriğe atlanır.</p>
<p>Not: Loop devre dışı bırakılır</p>
]]

function script_update(settings)
    source_name = obs.obs_data_get_string(settings, "source")
end

function script_description()
    return string.format(desc, tostring(fversion))
end

function script_properties()

    src_properties = obs.obs_properties_create()

    local p =
        obs.obs_properties_add_list(
        src_properties,
        "source",
        "Kaynak",
        obs.OBS_COMBO_TYPE_LIST,
        obs.OBS_COMBO_FORMAT_STRING
    )

    local obs_sources = obs.obs_enum_sources()

    if obs_sources ~= nil then
	
        obs.obs_property_list_add_string(p, "Devre Dışı", "disable")

        for _, source in ipairs(obs_sources) do
            local name = obs.obs_source_get_name(source)

            obs.obs_property_list_add_string(p, name, name)
        end
		
    end

    obs.source_list_release(obs_sources)

    return src_properties
	
end

function script_load(settings)
    script_update(settings)
end

function script_tick(seconds)

    if source_name == "disable" then
        return
    end

    local source = obs.obs_get_source_by_name(source_name)

    if source ~= nil then
	
        local state = obs.obs_source_media_get_state(source)

        if last_state ~= state then
		
            last_state = state

            if state == obs.OBS_MEDIA_STATE_STOPPED or state == obs.OBS_MEDIA_STATE_ENDED or state == obs.OBS_MEDIA_STATE_ERROR then
                obs.obs_source_media_next(source)
            end
			
        end
		
    end

    obs.obs_source_release(source)
	
end
